{
  "title": "WeDo Template: Image Journaling Protocol (Rules Pseudolanguage)",
  "description": "Distilling raw visual artifacts (.png, .jpg) from the inbox into the temporal Heartwood via iterative metabolic loops.",
  "storage_policy": {
    "global_template": "repos/diy-make/memory/public/json/wedo/image_journal.todo.json",
    "temporal_instance": "repos/diy-make/memory/public/<YEAR>/Q<N>/<MONTH>/<DAY>/json/<AGENT>_<SESSION>_image_journal.todo.json",
    "mandate": "Always copy the global template to the day's chrono-fractal json/ directory before starting work."
  },
  "required_files": {
    "formatter": "py/format_todo.py",
    "meta_protocol": "repos/diy-make/memory/public/json/wedo/meta_wedo.todo.json"
  },
  "process_protocol": {
    "source_inbox": "repos/diy-make/memory/inbox/png/",
    "loop_instructions": "Execute the following sequence in 3-by-3 metabolic batches to balance session velocity with human oversight:",
    "steps": [
      {
        "id": "STEP-00",
        "action": "Display ToDo",
        "command": "python3 py/format_todo.py <path_to_instance.json>",
        "description": "Print the current task list directly to the session feed for user review."
      },
      {
        "id": "STEP-01",
        "action": "Metabolic Batch Analysis",
        "description": "Read 3 artifacts from the inbox. Perform vision analysis on the batch. Post a high-fidelity summary of all 3 'straightup' for human-in-the-loop (HITL) oversight."
      },
      {
        "id": "STEP-02",
        "action": "Heartwood Graft",
        "command": "cp [artifact] repos/diy-make/memory/public/[YEAR]/Q[N]/[MONTH]/[DAY]/[ext]/[IDX]-[descriptive-name].[ext]",
        "description": "Copy the batch artifacts to their chronological destination. Artifacts MUST be renamed to user-friendly indexed names. Verify moves via 'Active Investigation' rule."
      },
      {
        "id": "STEP-03",
        "action": "Journal Hardening",
        "command": "cat [narrative] >> repos/diy-make/memory/public/[YEAR]/Q[N]/[MONTH]/[DAY]/md/wedo/daily_image_journal.md",
        "description": "Append the descriptive batch to the daily image journal (.md). Group entries by batch to maintain structural clarity."
      },
      {
        "id": "STEP-04",
        "action": "Inbox Clearance",
        "command": "mv [source_artifacts] repos/diy-make/memory/inbox/png/journaled/",
        "description": "Once hardening is verified, move the batch source files to the 'journaled/' sub-folder."
      }
    ]
  },
  "rules": {
    "3_by_3_compromise": "Batch operations in sets of 3 to prevent OOM-maxing and reduce human oversight fatigue.",
    "monotonic_growth": "Journal word count must only increase.",
    "active_investigation": "Verify existence of artifacts on localhost before inclusion.",
    "dual_referencing": "Reference both localhost paths and remotehost URLs."
  },
  "task_structure": {
    "id": "BATCH-[N]",
    "status": "[ ]",
    "description": "Metabolic Batch: [Summary of the 3 Visual Events]",
    "sub_tasks": [
      {
        "id": "IMG-[IDX]",
        "description": "[Detail]",
        "artifact_path": "[Path]"
      }
    ]
  },
  "attribution": "Heraclitus (20251230-100000)"
}

